import { useEffect } from 'react'
import './Accordion_Content.css'
import handler from './handler'

export function AQ({ text }) {
    return (
        <div>{text}</div>
    )
}

export default function AccordionContent({ type,text, src_audio, segments, id ,setIsAfterReq }) {
    useEffect(() => {
        handler()
    }, [])
    useEffect(() => {
        console.log(segments)
        console.log('00000')
    }, [segments])
    return (
        <div className="accordion_content">
            <div className="tabs-container">
                <div className="tabs_header">
                    <div className="tab-button active" data-tab="tab-simple">
                        <TabSimpleIcon />
                    </div>
                    <div className="tab-button" data-tab="tab-timed">
                        <TabTimedIcon />
                    </div>
                    <div style={{
                        display: 'flex', flexDirection: 'row', gap: '20px', alignContent: 'center'
                        , alignItems: 'center', width: '60%'
                    }}>
                        <RestartBtn setIsAfterReq={setIsAfterReq} type={type}/>
                        <CopyIcon />
                        <DownloadIcon />

                    </div>
                </div>

                <div className="tabs-body">

                    <div className="tab-content" id="tab-timed">

                        <div style={{ overflow: 'auto', maxHeight: '300px', height: '200px' }} >
                            <Segments segments={segments} />
                        </div>
                        <div className="media-player" style={{ marginLeft: '15%' }}>

                            <audio style={{ width: '90%' }}
                                id={id + "1"}
                                src={src_audio}
                                controls> </audio>
                        </div>
                    </div>

                    <div className="tab-content active" id="tab-simple">
                        <div
                            style={{ overflow: 'auto', maxHeight: '300px', height: '200px' }}>
                            <p className="simple-text-content">
                                {text}
                                
                            </p>
                        </div>
                        <div className="media-player" style={{ marginLeft: '15%' }}>
                            <audio style={{ width: '90%' }}
                                id={id + "2"}
                                src={src_audio}
                                controls> </audio>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    )
}

